sqlMap.select:
select DISTINCT t2.COD_TRANS_NRO, t3.COD_W from TRANSACCION_DETALLE t1 inner join TRANSACCION_NRO t2 on t1.COD_TRANSACCION=t2.COD_TRANSACCION inner join TRANSACCION t3 on t1.COD_TRANSACCION=t3.COD_TRANSACCION inner join(
select distinct t1.cod_trans_nro,t1.cod_trans_nro_ant,t3.cod_transaccion,t1.nro from adquisiciones.transaccion_detalle t3 left join adquisiciones.transaccion_detalle_nro t2 on t3.cod_trans_detalle=t2.cod_trans_detalle left join adquisiciones.transaccion_nro t1 on t1.cod_trans_nro= t2.cod_trans_nro left join adquisiciones.transaccion_envio_fecha t5 on t5.cod_trans_detalle=t3.cod_trans_detalle where t1.cod_tramite=2 AND (t5.estado='ALM1' or t5.estado='JUR')
)t4 on (t1.COD_TRANSACCION=t4.COD_TRANSACCION and t4.COD_TRANSACCION=#cod_transaccion#) where t1.COD_TRANSACCION=#cod_transaccion# and (t1.ESTADO = 'ALM1' or t1.ESTADO = 'C') and t2.COD_TRAMITE=2
